import './App.css';
import { useState } from 'react';
import AccessPage from './AccessPage';
import MainPage from './MainPage';
import ManagementPage from './ManagementPage';
import { Link, Route, Switch, useHistory } from 'react-router-dom';

//스테이트

function App() {
    function loadmonth() {
  let now = new Date();
  console.log(now)
  let month = now.getMonth();
  console.log(month);
  let season = selectseason(month);
    return month;
}
loadmonth();
if(loadmonth() === 1 );
function selectseason(month) {

  switch (month) {
    case 2:
    case 3:
    case 4:
      return "spring";
    case 5:
    case 6:
    case 7:
      return "summer";
    case 8:
    case 9:
    case 10:
      return "fall";
    case 11:
    case 0:
    case 1:
      return "winter";
  }
}
  return (
    <div>
      <Switch>
        <Route path='/manage/:major'>
          <ManagementPage></ManagementPage>
        </Route>

        <Route path='/main/:major'>
          <MainPage></MainPage>
        </Route>

        <Route path='/' >
          <AccessPage> </AccessPage>
        </Route>

      </Switch>


    </div>
  );
}

export default App;
