import './App.css';
import { useEffect, useState } from 'react';
import AccessPage from './AccessPage';
import MainPage from './MainPage';
import ManagementPage from './ManagementPage';
import { Link, Route, Switch, useHistory } from 'react-router-dom';

function loadmonth() {
        let now = new Date();
        console.log(now)
        let month = now.getMonth();
        console.log(month);
            return month;
    }
    function selectseason() {
        switch (loadmonth()) {
            case 0:
            case 1:
            case 2:
            return "quarter1";
            case 3:
            case 4:
            case 5:
            return "quarter2";
            case 6:
            case 7:
            case 8:
            return "quarter3";
            case 9:
            case 10:
            case 11:
            return "quarter4";
            default:
            return "";
        }
    }

function App() {
    useEffect(()=>{
        let quarter = selectseason();
        document.getElementById("quarterColor").style.color = "red";
    },[])

        console.log(document.getElementById("quarterColor"));

  return (
    <div>
        <div id = "quarterColor" style={{ display:"none"}}>PKSCL</div>
      <Switch>
        <Route path='/manage/:major'>
          <ManagementPage></ManagementPage>
        </Route>

        <Route path='/main/:major'>
          <MainPage></MainPage>
        </Route>

        <Route path='/' >
          <AccessPage> </AccessPage>
        </Route>

      </Switch>


    </div>
  );
}

export default App;
