import { useState } from 'react';
import log from './img/log.svg';
import { Nav } from 'react-bootstrap';
import { Link, Route, Switch, useHistory } from 'react-router-dom';
import axios from 'axios';

function AccessPage() {
  // let [signType, setSignType] =useState("signIn");
  let [position, setPosition] = useState("student");
  let [email, setEmail] = useState("");
  let [password, setPassword] = useState("");

  const history = useHistory();

  const onChange = (e) => {
    const img = e.target.files[0];
    const formData = new FormData();
    formData.append('file', img);
}

  return (
    <div className="container">
<div>
  <input type='file' 
      accept='image/jpg,impge/png,image/jpeg,image/gif' 
      name='profile_img' 
      onChange={onChange}>
  </input>
</div>
    </div>



  )
}


function login(email, password, position) {
  if (email === "" || password === "") {
    return (
      alert("아이디와 비밀번호를 모두 입력하세요")
    )
  }
  else {
    let loginInfo = { "email": email, "password": password, "position": position };
    // debugger;
    axios.post('https://pk-cog.url/login/' + position, loginInfo)
      .then((result) => {
        console.log(loginInfo);
      })
      .catch((error) => {
        console.log(loginInfo);
      });

  }


}

export default AccessPage;
