                setCurrentQuarter(selectedQuarter);
                defineColor(selectedQuarter);
            } else {
                alert("현재 공개된 장부가 아닙니다 :)")
            }
        }else{
                setCurrentQuarter(selectedQuarter);
                defineColor(selectedQuarter);
        }
    }

