                                                                                </div>

                                                                            </div>


                                                                            <div className="eventButtons">
                                                                                <button onClick={() => { eventDeleteButton(); }} style={{ marginRight: "15px" }}> <i class="far fa-trash-alt"></i> </button>
                                                                                <button onClick={() => {
                                                                                    //행사 수정 API
                                                                                        }} style={{ marginRight: "15px" }}> <i class="fas fa-check"></i> </button>
                                                                            </div>
                                                                        </div>

                                                                        <div>
                                                                                    <input type="text" style={{ border: "transparent", textAlign: "left", width: "650px" }} placeholder={props.editEventData["eventContext"]}
                                                                                        onInput={
                                                                                            (e) => {
                                                                                                changeEventContext(e.target.value);
                                                                                            }}></input>

                                                                        </div>

                                                                        {
                                                                            <div id="receiptContent" >
                                                                                    {
                                                                                        props.editEventData["receiptList"].map((receipt, j) => {
