                                                                                                                                        }
                                                                                                                                    </tbody>
                                                                                                                                        <div style={{ display: "flex", justifyContent: "center", width: "100%" }}>
                                                                                                                                            <button className='submitButton' type='button' style={{ margin: "0" }}
                                                                                                                                                onClick={() => {
                                                                                                                                                    var tempEditEventData  = { ...props.editEventData };

                                                                                                                                                    tempEditEventData["receiptList"][j]["receiptDetailList"].push({
                                                                                                                                                        context: "",
                                                                                                                                                        price: "",
                                                                                                                                                        amount: "",
                                                                                                                                                        totalAmount: ""
                                                                                                                                                    })
                                                                                                                                                    setEventData(tempEditEventData)
                                                                                                                                                }}>항목 추가</button></div>

                                                                                                                                </table>

                                                                                                                            </>
                                                                                                                            )
                                                                                                                    }


                                                                                                                </>)}
                                                                                                    </div>

                                                                                                            <div className="uploadimg">

                                                                                                                <label htmlFor='receiptImg'>
                                                                                                                    <img src={processImage(props.editEventData["receiptList"][j]["receiptImg"])} style={{ backgroundColor: "var(--color-leftPanel)" }}
                                                                                                                        alt={processImage(props.editEventData["receiptList"][j]["receiptImg"])} height={"150"} title='변경하시려면 클릭하세요.' />
                                                                                                                </label>
                                                                                                                <input type="file" id="receiptImg" accept="image/*"
                                                                                                                    onChange={(e) => { uploadImg(e.target.files[0], j); }} style={{ display: "none" }}></input>
                                                                                                            </div>
                                                                                                </div>

                                                                                            )
                                                                                        })
                                                                                    }
                                                                                </div>


                                                                        }

                                                                                <div style={{
                                                                                    width: "650px", display: "flex", justifyContent: "center", position: "relative",
                                                                                }}>
                                                                                    <button className='submitButton' type='button'
                                                                                        onClick={() => {
                                                                                            receiptAddButton();
                                                                                        }}><i class="fas fa-plus"></i></button>
                                                                                </div>

                                                                    </div>
                                                                    <div className="cardImg"></div>

                                                                </div>
                                                        )
                                                }
                                                </div>
                <div className="editEventBtns">
                <button className="editEventBtn">나가기</button>
                <button className="editEventBtn">저장하기</button>
                </div>
