import { useState } from 'react';
import log from './img/log.svg';
import { Nav } from 'react-bootstrap';
import { Link, Route, Switch, useHistory } from 'react-router-dom';
import axios from 'axios';

function AccessPage() {
  // let [signType, setSignType] =useState("signIn");
  let [position, setPosition] = useState("student");
  let [email, setEmail] = useState("");
  let [password, setPassword] = useState("");

  const history = useHistory();

  let[certFile, setCertFile] = useState("");

  function handleFileInput(e){
    setCertFile(e.target.files[0]);
  }

  function handlePost(){
    const formData = new FormData();
    formData.append('file', this.state.selectedFile);

    return axios.post("/api/upload", formData).then(res => {
      alert('성공')
    }).catch(err => {
      alert('실패')
    })
  }

  return (
    <div >

    <input type="file" name="file" onChange={e => handleFileInput(e)}/>
        <button type="button" style={{width: "20px",height: "20px"}} onClick={()=>handlePost()}/>

    </div>



  )
}


function login(email, password, position) {
  if (email === "" || password === "") {
    return (
      alert("아이디와 비밀번호를 모두 입력하세요")
    )
  }
  else {
    let loginInfo = { "email": email, "password": password, "position": position };
    // debugger;
    axios.post('https://pk-cog.url/login/' + position, loginInfo)
      .then((result) => {
        console.log(loginInfo);
      })
      .catch((error) => {
        console.log(loginInfo);
      });

  }


}

export default AccessPage;
