// import log from './img/log.svg';
// import {Nav} from 'react-bootstrap';
import './App.css';
import { useEffect, useState } from 'react';
import AccessPage from './AccessPage';
import MainPage from './MainPage';
import ManagementPage from './ManagementPage';
import { Link, Route, Switch, useHistory } from 'react-router-dom';

function loadmonth() {
        let now = new Date();
        console.log(now)
        let month = now.getMonth();
        console.log(month);
            return month;
    }
    function selectseason() {
        switch (loadmonth()) {
            case 0:
            case 1:
            case 2:
            return document.documentElement.style.setProperty("--quarterColor", "#c89034");
            case 3:
            case 4:
            case 5:
            return document.documentElement.style.setProperty("--quarterColor", "#578e5a");
            case 6:
            case 7:
            case 8:
            return document.documentElement.style.setProperty("--quarterColor", "#9b4346");
            case 9:
            case 10:
            case 11:
            return document.documentElement.style.setProperty("--quarterColor", "#597082");
            default:
            return "";
        }
    }

function App() {
    useEffect(()=>{
        selectseason();
    },[])

  return (
    <div>
      <Switch>
        <Route path='/manage/:major'>
          <ManagementPage></ManagementPage>
        </Route>

        <Route path='/main/:major'>
          <MainPage></MainPage>
        </Route>

        <Route path='/' >
          <AccessPage> </AccessPage>
        </Route>

      </Switch>


    </div>
  );
}

export default App;
