                                                                                        onInput={
                                                                                            (e) => {
                                                                                                changeEventItem(e.target.value,"eventContext");
                                                                                            }}></input>

                                                                        </div>

                                                                        {
                                                                            <div id="receiptContent" >
                                                                                    {
                                                                                        eventData["receiptList"].map((receipt, j) => {
                                                                                            return (
                                                                                                <div className="receiptCard" key={j}>
                                                                                                    <div className="receiptResource">
