                                                                                                                                    onInput={
                                                                                                                                        (e) => {
                                                                                                                                            changeReceiptItem(e.target.value,j,"receiptContext");
                                                                                                                                        }}>
                                                                                                                                </input>
                                                                                                                            </div>


                                                                                                                    {
