      eventSum.push(sumEvent(eventList[i]["receiptList"]));
    }

    setEventAmount([...eventSum]);
    let quarterSum = 0;

    for (let i = 0; i < eventSum.length; i++) {
      quarterSum = quarterSum + eventSum[i];
    }

    setQuarterAmount(quarterSum);
  }

